import React, { useState } from "react";
import Dropdown from "./Dropdown";
import classes from "./SearchMovie.module.css";

import { Pane, SearchInput } from "evergreen-ui";


export default function SearchMovie(props) {
  const [query, setQuery] = useState("");
  const [newMovieId, setNewMovieId] = useState(0);


  const onSubmitHandler = () => {
    console.log('adding Movie ', newMovieId);
  }

  const onChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  const onDropdownHandler = (event) => {
    setNewMovieId(event.target.value);
    console.log('you have selected ', newMovieId);
    //setResults([]);
  };

  return (


    <Pane className={classes.container}>

      <form onSubmit={onSubmitHandler}>
        <SearchInput
          className={classes["search-input"]}
          type="text"
          name="query"
          placeholder="i.e. Jurassic Park"
          onChange={onChangeHandler}
          value={query}
        />
        </form>

        {query && <Dropdown onDropdownHandler = {onDropdownHandler} query={query}/>}


    </Pane>
  );
}
