import "./App.css";

import Movies from "./components/Movies";

import { EasybaseProvider, useEasybase } from "easybase-react";
import ebconfig from "./ebconfig";

const App = () => {
  const eb = useEasybase();
  const {e, db} = eb;


  const Button=  () => {

    const onClickHandler =  () => {
      db("MOVIES")
      .insert({
        id: "id",
        title: "title",
        genres: "genres",
        posterpath: "poster_path",
        overview: "overview",
        voteaverage: Number(Math.random()),
        watched: false,
      })
      .one();
    }
    return <button type="button" onClick={onClickHandler}>add</button>
  }

    return (
      <EasybaseProvider ebconfig={ebconfig}>
      <Movies />
      <Button/>
    </EasybaseProvider>
  );
};

export default App;
