import React, { useState } from "react";

import SearchMovie from "./Movie/SearchMovie";
import classes from "./Movies.module.css";
import Movie from "./Movie/Movie";

import { useEasybase } from "easybase-react";


const Movies = () => {
  const [refresh, setRefresh] = useState(false);

  const { e, useReturn } = useEasybase();

  const table = useEasybase().db("MOVIES");

  const { frame } = useReturn(
    () => table.return().orderBy({ by: "watched", sort: "asc" }),
    [refresh]
  );

  const headers = (
    <thead>
      <tr>
        <th>watched</th>
        <th>title</th>
        <th>genre</th>
        <th>poster</th>
        <th>delete</th>
      </tr>
    </thead>
  );




  const delMovie = async (id) => {
    let singleRecord = await table().return().where(e.eq("id", id)).one();

    await table.delete().where({ _key: singleRecord._key }).one();

    setRefresh((st) => !st);
  };

  const markWatched = async (id) => {
    let singleRecord = await table().return().where(e.eq("id", id)).one();
    let watched = singleRecord.watched;

    await table
      .where({ _key: singleRecord._key })
      .set({ watched: !watched })
      .one();

    setRefresh((st) => !st);
  };

  return (
    <div className={classes.container}>
      {/* TODO:fix first call and call when non empty */}
      <SearchMovie  />

      <table className={classes.movies}>
        {headers}
        <tbody>
          {frame.map((movie, index) => {
            return (
              <Movie
                key={movie.id}
                movie={movie}
                delMovie={delMovie}
                markWatched={markWatched}
                // index={movie["_key"]}
              />
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default Movies;
