import React, { useState } from "react";
import classes from "./Movie.module.css";

import DeleteButton from "../../UI/DeleteButton";

import Overview from "./Overview";
import { Table, Checkbox } from "evergreen-ui";

const Movie = (props) => {
  const [details, setDetails] = useState(false);
  // const onDeleteHandler = () => {
  //   props.delMovie(props.movie.id);
  //   console.log(`delete`, props.movie.id);
  // };

  const onCheckHandler = () => {
    props.markWatched(props.movie.id);
  };

  const onClickHandler = () => {
    setDetails(st => !st);
  };

  const onMouseLeaveHandler = () => {
    setDetails(false);
  };

  return (
    <>
    <Table.Row key={props.movie._id}
    onClick={onClickHandler} onMouseLeave={onMouseLeaveHandler}>


      <Table.Cell className={classes.watched}>
        <Checkbox
          name="watched"
          type="checkbox"
          onChange={onCheckHandler}
          checked={props.movie.watched}
          value={props.movie.watched}
        />
      </Table.Cell>
      <Table.TextCell>{props.movie.title}</Table.TextCell>
      <Table.TextCell>{props.movie.genres}</Table.TextCell>
      <Table.TextCell>{props.movie.overview}</Table.TextCell>
      <Table.TextCell>{props.movie.voteaverage}</Table.TextCell>
      <Table.Cell className={classes.poster}>
        <img
          className={classes.poster}
          src={`https://image.tmdb.org/t/p/original${props.movie.posterpath}`}
          alt="poster"
        />
      </Table.Cell>
      <Table.Cell className={classes["delete-button"]}>
        <DeleteButton movie={props.movie} />
      </Table.Cell>
    </Table.Row>

    {details && (
      <Table.Row>
        <Overview movie = {props.movie}/>
      </Table.Row>
    )}
    </>
  );
};

export default Movie;
