import { useState } from "react";
import "./App.css";
import Movies from "./components/Movies";
import SearchMovie from "./components/Movie/SearchMovie";
//import MovieGenres from "./components/oldstuff/MovieGenres";
// import DUMMY_MOVIES from "./resources/dummy_movies";

const fetchGenres = async (e) => {
 //e.preventDefault();
  console.log("fetching genres");

  const url = `https://api.themoviedb.org/3/genre/movie/list?api_key=5dcf7f28a88be0edc01bbbde06f024ab&language=en-US&id=14`;

  try {
    const res = await fetch(url);
    const data = await res.json();
    console.log(data);
    return data.genres;
  } catch (err) {
    console.error(err);
    return [];
  }
};

const getGenres = (e,genre_ids) => {
  const genres = fetchGenres(e);
  let genres_array = [];
  genre_ids.map((id) => {
    genres_array.push(genres[id]);
  });

  return genres_array.split(0, 2);
};

const App = () => {
  const [movies, setMovies] = useState([]);

  const movieExists = (id) => {
    return movies.some((movie) => {
      return movie.id === id;
    });
  };

  const addMovie = (movie) => {
    // console.log(`movie from App`, movie.poster_path);

    if (movieExists(movie.id)) {
      console.log(`duplicate!`, movie);
      return;
    }

    let genres = getGenres(event,movie.genre_ids);

    let newMovie = {
      // id: movie.id, // uuidv4(),
      // title: movie.title,
      // genre: movie.genre, // TODO lookup genre
      // poster_path: movie.poster_path,
      ...movie,
      genres: genres,
      watched: false,
    };

    setMovies([...movies, newMovie]);
  };

  const delMovie = (id) => {
    setMovies(movies.filter((movie) => movie.id !== id));
  };

  const markWatched = (id) => {
    setMovies(
      movies.map((movie) =>
        movie.id === id ? { ...movie, watched: !movie.watched } : movie
      )
    );
  };

  return (
    <div>
      <SearchMovie onSelect={addMovie} />
      {/* <MovieForm addMovie={addMovie} /> */}
      <Movies movies={movies} delMovie={delMovie} markWatched={markWatched} />
    </div>
  );
};

export default App;
