import React, { useState, useEffect } from "react";
import AddMovieButton from "../../UI/AddMovieButton";
import classes from "./SearchMovie.module.css";

const sortResults = (results) => {
  results.sort((a, b) => b.vote_average - a.vote_average);
  return results;
};

export default function SearchMovie(props) {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const delayDebounceFn = setTimeout(async () => {
      console.log(query);
      const url = `https://api.themoviedb.org/3/search/movie?api_key=5dcf7f28a88be0edc01bbbde06f024ab&language=en-US&query=${query}&page=1&include_adult=false`;
      try {
        const res = await fetch(url);
        const data = await res.json();
        console.log(data);
        if (data.results && data.results.length > 0) {
          setResults(data.results);
          setIsVisible(true);
        }
      } catch (err) {
        console.error(err);
        setIsVisible(false);
      }
    }, [1000]);

    return () => {
      clearTimeout(delayDebounceFn);
      setIsVisible(false);
    };
  }, [query]);

  const onChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  return (
    <div className={classes.container}>
      <label className={classes["search-label"]} htmlFor="query">
        Movie Name
      </label>
      <input
        className={classes["search-input"]}
        type="text"
        name="query"
        placeholder="i.e. Jurassic Park"
        onChange={onChangeHandler}
        value={query}
      />

      {isVisible && results && results.length > 0 && (
        <div className={classes.dropdown}>
          <ul onClick={() => setIsVisible(false)}>
            {sortResults(results).map((result) => (
              <li>
                <AddMovieButton movie={result} />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
