import React, {useEffect} from 'react';

import classes from "./Overview.module.css";

import { TMDB_API_KEY } from "../../store/keys";

const Overview = (props) => {
  let movie;
  useEffect(() => {
    const getDetails = async (movie_id) => {
      const url = `https://api.themoviedb.org/3/movie/${movie_id}?api_key=${TMDB_API_KEY}&language=en-US`;
      try {
        const res = await fetch(url);
        const data = await res.json();
        console.log(data);

          return(data);

      } catch (err) {
        console.error(err);
      }
    }
    movie = getDetails(props.movie_id);

    // return () => {
    //   cleanup
    // };
  }, []);




  return (
    <div className={classes["container"]}>
      <img
        className={classes.poster}
        src={`https://image.tmdb.org/t/p/original${movie.posterpath}`}
        align="left"
        alt="poster"
      />
      <div className={classes.overview}>
        <h2>{movie.title}</h2>
        <p>{movie.overview}</p>
        <p>vote average: {movie.vote_average} - vote count: {movie.vote_count}</p>
        <div className={classes.financials}>budget: {movie.budget}</div>
      </div>
    </div>
  );
};

export default Overview;
