import React, { useState } from "react";
import Dropdown from "./Dropdown";
import classes from "./SearchMovie.module.css";

import { Pane, SearchInput } from "evergreen-ui";

import GENRES from "../../resources/genres";
import db from "../../store/mongodb";

const getGenres = (genre_ids) => {
  let genres_array = [];
  genre_ids.map((id) => {
    const result = GENRES.find((genre) => genre.id === id);
    genres_array.push(result.name);
    return genres_array;
  });

  console.log(genres_array.toString());
  return genres_array.toString();
};

export default function SearchMovie(props) {
  const [query, setQuery] = useState("");
  const [newMovie, setNewMovie] = useState(0);

  const addMovie = async ({ movie }) => {
    console.log(`adding `, movie.title);

    const genres = getGenres(movie.genre_ids);
    await db
      .collection("movies")
      .insertOne({
        id: movie.id,
        title: movie.title,
        genres: genres,
        posterpath: movie.poster_path,
        overview: movie.overview,
        voteaverage: Number(movie.vote_average),
        watched: false,
      })
      .then((result) =>
        console.log(`Successfully inserted item with _id: ${result.insertedId}`)
      )
      .catch((err) => console.error(`Failed to insert item: ${err}`));
  };

  const onSubmitHandler = () => {
    console.log("adding Movie ", newMovie.id);
    //setQuery("");
    addMovie(newMovie);
    //setNewMovie();
  };

  const onChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  const onDropdownHandler = (movie) => {
    setNewMovie(movie);
    console.log("in SearchMovie you have selected ", newMovie.id);
    setQuery(movie.title);
  };

  return (
    <Pane className={classes.container}>
      <form onSubmit={onSubmitHandler}>
        <SearchInput
          className={classes["search-input"]}
          type="text"
          name="query"
          placeholder="i.e. Jurassic Park"
          onChange={onChangeHandler}
          value={query}
        />
        <input type="submit" value="OK" />√ç
      </form>

      {query && (
        <Dropdown onDropdownHandler={onDropdownHandler} query={query} />
      )}
    </Pane>
  );
}
