import React, { useState, useEffect } from "react";
import api from "./api/queries";

import SearchMovie from "./components/Movie/SearchMovie";
import MovieList from "./components/Movie/MovieList";

import GENRES from "./resources/genres";

const getGenres = (genre_ids) => {
  let genres_array = [];
  genre_ids.map((id) => {
    const result = GENRES.find((genre) => genre.id === id);
    genres_array.push(result.name);
    return genres_array;
  });

  console.log(genres_array.toString());
  return genres_array.toString();
};

function App() {

  const [movies, setMovies] = useState([]);

  useEffect(() => {
    retrieveMovies();
  }, []);

  const retrieveMovies = async () => {
    await api
      .getAllMovies()
      .then((response) => {
        setMovies(response.data.data);
      })
      .catch((error) => {
        console.log(`error`, error);
      });
  };

  const addMovie = async (movie) => {
    console.log(`adding `, movie);

    const genres = getGenres(movie.genre_ids);

    const payload = {
      id: movie.id,
      title: movie.title,
      genres: genres,
      poster_path: movie.poster_path,
      overview: movie.overview,
      vote_average: Number(movie.vote_average),
      watched: false,
    };

    await api.insertMovie(payload).then((res) => {
      setMovies([...movies, movie]);
      console.log(`res`, res.id)
    });
  };

  const onAddHandler = async (movie) => {
    console.log(`in App, adding: `, movie);
    const lookup = await api.getMovieByTMDBId(movie.id).catch((error) => {
      console.log(`error`, error);
    });

    lookup == null ? addMovie(movie) : console.log(`dupe`, movie);
  };

  const onWatchedHandler = async (movie) => {
    const payload = { ...movie };
    payload.watched = !payload.watched;

    await api.updateMovieById(movie._id, payload);
    // .then((res) => {
    //   window.alert(`Movie updated successfully`);
    // });
    const newMovies = [...movies];
    newMovies.splice(payload.id,1)
    setMovies([...newMovies, payload]);

  };

  const onRemoveHandler = async (movie) => {
    const newMovies = [...movies];
    await api
      .deleteMovieById(movie._id)
      .then((result) =>
        console.log(`Successfully deleted item with _id: ${movie._id}`)
      )
      .catch((err) => console.error(`Failed to delete item: ${err}`));

    newMovies.splice(movie.id,1)
    setMovies(newMovies)
    //movies.filter(item => item._id !== movie._id));
  };

  return (
    <div>
      <MovieList
        movies={movies}
        onWatchedHandler={onWatchedHandler}
        onRemoveHandler={onRemoveHandler}
      />
      <SearchMovie onAddHandler={onAddHandler} />
    </div>
  );
}

export default App;
