import React, { useState, useEffect } from "react";
import Dropdown from "./Dropdown";
import classes from "./SearchMovie.module.css";

import { Pane, SearchInput } from "evergreen-ui";


export default function SearchMovie(props) {
  const [query, setQuery] = useState("");
  const [newMovieId, setNewMovieId] = useState(0);
  const [results, setResults] = useState([]);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const delayDebounceFn = setTimeout(async () => {
      console.log(query);
      const url = `https://api.themoviedb.org/3/search/movie?api_key=5dcf7f28a88be0edc01bbbde06f024ab&language=en-US&query=${query}&page=1&include_adult=false`;
      try {
        const res = await fetch(url);
        const data = await res.json();
        console.log(data);
        if (data.results && data.results.length > 0) {
          setResults(data.results);
          setIsVisible(true);
        }
      } catch (err) {
        console.error(err);
        // setIsVisible(false);
      }
    }, [1000]);

    return () => {
      clearTimeout(delayDebounceFn);
      // setIsVisible(false);
    };
  }, [query]);

  const onSubmitHandler = () => {
    console.log('adding Movie ', newMovieId);
  }

  const onChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  const onDropdownHandler = (event) => {
    setNewMovieId(event.target.value);
    console.log('you have selected ', newMovieId);
    //setResults([]);
  };

  return (


    <Pane className={classes.container}>

      <form onSubmit={onSubmitHandler}>
        <SearchInput
          className={classes["search-input"]}
          type="text"
          name="query"
          placeholder="i.e. Jurassic Park"
          onChange={onChangeHandler}
          value={query}
        />
        </form>

        <Dropdown onDropdownHandler = {onDropdownHandler} query={query}/>


    </Pane>
  );
}
