import React from "react";

const Movie = (props) => {
  const onDeleteHandler = () => {
    props.delMovie(props.movie.id);
    console.log(`delete`, props.movie.id);
  };

  const onCheckHandler = () => {
    props.markWatched(props.movie.id);
  };

  const getImage = () => {
    let imageUrl = `https://image.tmdb.org/t/p/original${props.movie.poster_path}`;
    console.log(`url`, imageUrl);
    return imageUrl;
  };

  const url="/wuMc08IPKEatf9rnMNXvIDxqP4W.jpg";
  return (
    <li>
      <input
        name="watched"
        type="checkbox"
        onChange={onCheckHandler}
        checked={props.movie.watched}
        value={props.movie.watched}
      />
      <span>{props.movie.title}</span>
      <span>{props.movie.genre}</span>
      <span>
        <img src={`https://image.tmdb.org/t/p/original${url}`} alt="poster" width="30" height="45" />
      </span>
      <span>
          <img src="https://image.tmdb.org/t/p/original" width="30" height="45"/>
          </span>
      <button onClick={onDeleteHandler}>Delete</button>
    </li>
  );
};

export default Movie;
