import React from "react";
import "./App.css";
import Movies from "./components/Movies";
// import {addMovies} from "./Database";

import * as Realm from "realm-web";

const REALM_APP_ID = "realm-movie-nite-dtfju"; // e.g. myapp-abcde
const app = Realm.App.getApp({ id: REALM_APP_ID });

// Create a component that displays the given user's details
function UserDetail({ user }) {
  return (
    <div>
      <p>Logged in with anonymous id: {user.id}</p>
    </div>
  );
}
// Create a component that lets an anonymous user log in
function Login({ setUser }) {
  const loginAnonymous = async () => {
    const user = await app.logIn(Realm.Credentials.anonymous());
    setUser(user);
  };
  return <button onClick={loginAnonymous}>Log In</button>;
}

// Declare Schema
const MovieSchema = {
  name: "Movies",
  properties: {
    id: "int",
    title: "string",
    genres: "string",
    overview: "string",
    posterpath: "string",
    voteaverage: "int",
    watched: "boolean",
  },
};

const App = () => {
  // Keep the logged in Realm user in local state. This lets the app re-render
  // whenever the current user changes (e.g. logs in or logs out).
  const [user, setUser] = React.useState(app.currentUser);

  const mongodb = user.mongoClient("mongodb-atlas");

  const addMovies = () => {
    const movies = mongodb.db("movie-nite").collection("movies");
    for (let i = 0; i < 3; i++) {
      movies.insertOne({
        title: "Harry Putter " + i,
        overview: "bla bla",
      });
    }
  };

  // If a user is logged in, show their details.
  // Otherwise, show the login screen.
  return (
    <div className="App">
      <div className="App-header">
        {user ? <UserDetail user={user} /> : <Login setUser={setUser} />}
      </div>
      <button type="button" onClick={addMovies}>
        Add movies
      </button>
    </div>
  );
};

export default App;
