import { useEasybase } from "easybase-react";

import classes from "./AddMovieButton.module.css";

import GENRES from "../resources/genres";

const getGenres = (genre_ids) => {
  let genres_array = [];
  genre_ids.map((id) => {
    const result = GENRES.find((genre) => genre.id === id);
    genres_array.push(result.name);
    return genres_array;
  });

  console.log(genres_array.toString());
  return genres_array.toString();
};

const AddMovieButton = (props) => {
  const { db, e } = useEasybase();

  const movieExists = async (id) => {
    console.log("check if movie exists :>> ", id);
    // try {
    let singleRecord = await db("MOVIES").return().where(e.eq("id", id)).one();
    console.log(`singleRecord`, singleRecord);
    return singleRecord !== null;
    // } catch (exception) {
    //   console.log(`movie does not exist in your list`, id);
    // }
    // return false;
  };

  const onAddMovie = async ({
    id,
    title,
    genre_ids,
    poster_path,
    overview,
    vote_average,
  }) => {
    if(movieExists(id)) {
      console.log(`duplicate movie`,  title)

    };

    const genres = getGenres(genre_ids);

    try {
      await db("MOVIES")
        .insert({
          title: title,
          genres: genres,
          posterpath: poster_path,
          watched: false,
          overview: overview,
          voteaverage: Number(vote_average),
          id: id,
        })
        .one();
    } catch (_) {
      alert("Error");
    }
  };

  return (
    <div
      key={props.movie.id}
      className={classes.moviebutton}
      onClick={onAddMovie.bind(null, props.movie)}
    >
      <div className={classes.title}>{props.movie.title}</div>
      <div className={classes.vote}>{props.movie.vote_average}</div>
    </div>
  );
};

export default AddMovieButton;
