import React, { useState, useEffect } from "react";

import classes from "./Dropdown.module.css";

const sortResults = (results) => {
  results.sort((a, b) => b.vote_average - a.vote_average);
  return results;
};

const Dropdown = (props) => {
  const [results, setResults] = useState([]);
  // const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const delayDebounceFn = setTimeout(async () => {
      console.log("in dropdown, query = " , props.query);
      const url = `https://api.themoviedb.org/3/search/movie?api_key=5dcf7f28a88be0edc01bbbde06f024ab&language=en-US&query=${props.query}&page=1&include_adult=false`;
      try {
        const res = await fetch(url);
        const data = await res.json();
        console.log(data);
        if (data.results && data.results.length > 0) {
          setResults(data.results);
          //   setIsVisible(true);
        }
      } catch (err) {
        console.error(err);
        // setIsVisible(false);
      }
    }, [1000]);

    return () => {
      clearTimeout(delayDebounceFn);
      // setIsVisible(false);
    };
  }, [props.query]);

  const onDropdownHandler = (id) => {
      console.log("in Dropdown you selected : ", id)
    props.onDropdownHandler(id);
  };

  return (
    <div className={classes.dropdown}>
      <ul onClick={props.onClick}>
        {sortResults(results).map((result) => (
          <li key={result.id} onClick={() => onDropdownHandler(result.id)}>

      <div className={classes.title}>{result.title}</div>
      <div className={classes.vote}>{result.vote_average}</div>

            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dropdown;
