import React, { useState, useEffect } from "react";
// import { useMongoDB } from "./store/mongodb";
// import { useRealmApp } from "./store/realm";
import api from "./api/queries";

import SearchMovie from "./components/Movie/SearchMovie";
import MovieList from "./components/Movie/MovieList";

import GENRES from "./resources/genres";

const getGenres = (genre_ids) => {
  let genres_array = [];
  genre_ids.map((id) => {
    const result = GENRES.find((genre) => genre.id === id);
    genres_array.push(result.name);
    return genres_array;
  });

  console.log(genres_array.toString());
  return genres_array.toString();
};

function App() {
  // const { loginApiKey, logOut, user } = useRealmApp();
  // const { db } = useMongoDB();

  // handleLogIn();

  const [movies, setMovies] = useState(api.getAllMovies());

  //const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    retrieveMovies();
  },[])

  const retrieveMovies = () => {
     api.getAllMovies().then(response => {setMovies(response)
     }).catch((error) => {console.log(`error`, error)})
  //     setMovies(authoredMovies);
  }

  const addMovie = async (movie) => {
    console.log(`adding `, movie);

    const genres = getGenres(movie.genre_ids);

    const payload = {
      id: movie.id,
      title: movie.title,
      genres: genres,
      poster_path: movie.poster_path,
      overview: movie.overview,
      vote_average: Number(movie.vote_average),
      watched: false,
    };

    await api.insertMovie(payload).then((res) => {
      window.alert(`Movie inserted successfully`);

      setMovies([...movies,movie]);
    });
  };

  // useEffect(() => {
  //   async function wrapMovieQuery() {
  //     // if (user && db) {
  //     //   const authoredMovies = await db.collection("movies").find({},{sort:{ "watched": 1 }});
  //     //   setMovies(authoredMovies);
  //     // }
  //     const authoredMovies = await api.getAllMovies();
  //     setMovies(authoredMovies);
  //   }
  //   wrapMovieQuery();
  // }, [refresh]);

  // async function handleLogIn() {

  //   await loginApiKey();
  // }

  const isDuplicate = async (movie) => {
    const dupe = await api.getMovieById(movie.id);

    // db.collection("movies").findOne({ id: movie.id });
    console.log("dupe?", dupe);

    return !(dupe === null);
  };

  const onAddHandler = async (movie) => {
    console.log(`in App, adding: `, movie);
    // let dupe = await isDuplicate(movie);
    // dupe ? console.log(`dupe`, movie) : addMovie(movie);
    addMovie(movie);
    // setRefresh((st) => !st);
  };

  const onWatchedHandler = async (movie) => {
    const payload = { ...movie };
    payload.watched = !payload.watched;

    await api.updateMovieById(movie.id, payload).then((res) => {
      window.alert(`Movie updated successfully`);
    });

    // await db
    //   .collection("movies")
    //   .updateOne({ id: movie.id }, { $set: { watched: !movie.watched } })
    //   .then((result) =>
    //     console.log(`Successfully flagged item with _id: ${result.insertedId}`)
    //   )
    //   .catch((err) => console.error(`Failed to flag item: ${err}`));
  };

  const onRemoveHandler = async (movie) => {
    await api
      .deleteMovieById(movie.id)
      .then((result) =>
        console.log(`Successfully deleted item with _id: ${result.insertedId}`)
      )
      .catch((err) => console.error(`Failed to delete item: ${err}`));
  };

  // return user && db && user.state === "active" && (
  return (
    <div>
      {movies && (
        <MovieList
          movies={movies}
          // user={user}
          // logOut={logOut}
          onWatchedHandler={onWatchedHandler}
          onRemoveHandler={onRemoveHandler}
        />
      )}
      <SearchMovie onAddHandler={onAddHandler} />
    </div>
  );
}

export default App;
