import { useEasybase, EasybaseProvider, callFunction } from "easybase-react";
// import ebconfig from "../../ebconfig";
import classes from "./AddMovieButton.module.css";

import GENRES from "../../resources/genres";

const getGenres = (genre_ids) => {
  let genres_array = [];
  genre_ids.map((id) => {
    const result = GENRES.find((genre) => genre.id === id);
    genres_array.push(result.name);
    return genres_array;
  });

  console.log(genres_array.toString());
  return genres_array.toString();
};

const AddMovieButton = (props) => {

  const { db } = useEasybase();
  const table = useEasybase({ ebconfig }).db("MOVIES");


  const movieExists = async (id) => {
    try {
      let singleRecord = await table().return().where(e.eq("id", id)).one();
      return singleRecord !== null;
    } catch (exception) {}
    return false;
  };



  const onAddMovie = (movie) => {
    if(movieExists(movie.id)) return;

    let genres = getGenres(movie.genre_ids);
    db("MOVIES")
      .insert({
        title: movie.title,
        genres: genres,
        posterpath: movie.poster_path,
        watched: false,
        overview: movie.overview,
        voteaverage: Number(movie.vote_average),
        id: movie.id,
      })
      .one();
  };

  return (
    <tr
      key={props.movie.id}
      className={classes.tr}
      onClick={onAddMovie.bind(null, props.movie)}
    >
      <td>{props.movie.title}</td>
      <td className={classes.vote}>{props.movie.vote_average}</td>
    </tr>
  );
};

export default AddMovieButton;
