                            refreshToken: g.refreshToken,
                            token: g.token
                        });

                        if (req_res.success) {
                            g.token = req_res.data.token
                            g.newTokenCallback();
                            return tokenPost(postType, body);
                        } else {
                            g.token = "";
                            g.refreshToken = "";
                            g.newTokenCallback();
