import React, { useState, useEffect } from "react";

import SearchMovie from "./Movie/SearchMovie";
import classes from "./Movies.module.css";
import Movie from "./Movie/Movie";



const Movies = (props) => {
  const [refresh, setRefresh] = useState(false);

  const mongodb = props.user.mongoClient("mongodb-atlas");

  const moviesCollection = mongodb.db("movie-nite").collection("movies");

  const { movies } = useEffect(
    () => {const query = {  };
    const projection = { };
    return moviesCollection.find(query, projection)
      .sort({ title: 1 })
      .toArray()
      .then(items => {
        console.log(`Successfully found ${items.length} documents.`)
        items.forEach(console.log)
        return items
      })
      .catch(err => console.error(`Failed to find documents: ${err}`))},
    [refresh]
  );

  const headers = (
    <thead>
      <tr>
        <th>watched</th>
        <th>title</th>
        <th>genre</th>
        <th>poster</th>
        <th>delete</th>
      </tr>
    </thead>
  );

  const markWatched = async (id) => {
    // let singleRecord = await table().return().where(e.eq("id", id)).one();
    // let watched = singleRecord.watched;

    // await table
    //   .where({ _key: singleRecord._key })
    //   .set({ watched: !watched })
    //   .one();

    setRefresh((st) => !st);
  };

  return (
    <div className={classes.container}>
      {/* TODO:fix first call and call when non empty */}
      <SearchMovie  />

      <table className={classes.movies}>
        {headers}
        <tbody>
          {movies.map((movie, index) => {
            return (
              <Movie
                key={movie.id}
                movie={movie}
                markWatched={markWatched}
                // index={movie["_key"]}
              />
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default Movies;
