import React, { useState, useEffect } from "react";
import "./App.css";
import Movies from "./components/Movies";
import SearchMovie from "./components/Movie/SearchMovie";



const App = () => {
  const [movies, setMovies] = useState([]);
  const [appState, setAppState] = useState({ loading: false, genres: null });

  useEffect(() => {
    setAppState({ loading: true });
    const apiUrl = `https://api.themoviedb.org/3/genre/movie/list?api_key=5dcf7f28a88be0edc01bbbde06f024ab&language=en-US&id=14`;
    fetch(apiUrl)
      .then((res) => res.json())
      .then((genres) => {
        setAppState({ loading: false, genres: genres });
      });
  }, [setAppState]);

  const movieExists = (id) => {
    return movies.some((movie) => {
      return movie.id === id;
    });
  };

  const getGenres = (genre_ids) => {
    let genres_array = [];
    genre_ids.map((id) => {
      return genres_array.push(appState.genres[id]);
    });

    return genres_array.slice(0, 2);
  };

  const addMovie = (movie) => {
    // console.log(`movie from App`, movie.poster_path);

    if (movieExists(movie.id)) {
      console.log(`duplicate!`, movie);
      return;
    }

    let genres = getGenres(movie.genre_ids);

    let newMovie = {
      ...movie,
      genres: genres,
      watched: false,
    };

    setMovies([...movies, newMovie]);
  };

  const delMovie = (id) => {
    setMovies(movies.filter((movie) => movie.id !== id));
  };

  const markWatched = (id) => {
    setMovies(
      movies.map((movie) =>
        movie.id === id ? { ...movie, watched: !movie.watched } : movie
      )
    );
  };

  return (
    <div>
      <SearchMovie onSelect={addMovie} />
      {/* <MovieForm addMovie={addMovie} /> */}
      <Movies movies={movies} delMovie={delMovie} markWatched={markWatched} />
    </div>
  );
};

export default App;
