import React from "react";
import classes from "./Movie.module.css";

const Movie = (props) => {
  const onDeleteHandler = () => {
    props.delMovie(props.movie.id);
    console.log(`delete`, props.movie.id);
  };

  const onCheckHandler = () => {
    props.markWatched(props.movie.id);
  };

  return (
    <tr>
      <td>
        <input
          name="watched"
          type="checkbox"
          onChange={onCheckHandler}
          checked={props.movie.watched}
          value={props.movie.watched}
        />
      </td>
      <td>{props.movie.title}</td>
      <td>{props.movie.genres}</td>
      <td>
        <img className={classes.poster}
          src={`https://image.tmdb.org/t/p/original${props.movie.poster_path}`}
          alt="poster"
          width="30"
          height="45"
        />
      </td>
      <td>
        <button className={classes.deleteButton} onClick={onDeleteHandler}>Delete</button>
      </td>
    </tr>
  );
};

export default Movie;
