import React, { useState, useEffect } from "react";
import ReactDOM from 'react-dom';

import classes from "./Overview.module.css";

import { TMDB_API_KEY } from "../../store/keys";

const Backdrop = props => {
  return <div className={classes.backdrop} onClick={props.onClick}></div>;

};

const OverviewModal = (props) => {
  const [movie, setMovie] = useState({});

  useEffect(() => {
    const getDetails = async () => {
      const url = `https://api.themoviedb.org/3/movie/${props.movie_id}?api_key=${TMDB_API_KEY}&language=en-US`;

      const res = await fetch(url);
      const data = await res.json();
      console.log(data);

      setMovie(data);
    };
    getDetails();
  }, [props.movie_id]);

  const details = (
    <div className={classes.modal}>
      <img
        className={classes.poster}
        src={`https://image.tmdb.org/t/p/original${movie.posterpath}`}
        align="left"
        alt="poster"
      />
      <div className={classes.overview}>
        <h2>{movie.title}</h2>
        <p>{movie.overview}</p>
        <p>
          vote average: {movie.vote_average} - vote count: {movie.vote_count}
        </p>
        <div className={classes.financials}>budget: {movie.budget}</div>
      </div>

      <div>
        <button onClose={props.onClose}>Close</button>
      </div>
    </div>
  );
  return <>{props.show && details}</>;
};


const portalElement = document.getElementById('overlays');

const Overview = (props) => {
    return(
        <>
{ReactDOM.createPortal(<Backdrop onClick={props.onBackdropClick}/>,portalElement)}
{ReactDOM.createPortal(<OverviewModal>{props.children}</OverviewModal>, portalElement)}
        </>
    );
}

export default Overview;
