import classes from "./AddMovieButton.module.css";

import GENRES from "../resources/genres";

import { useMongoDB } from "../store/mongodb";
import { useRealmApp } from "../store/realm";
const { db } = useMongoDB();

const getGenres = (genre_ids) => {
  let genres_array = [];
  genre_ids.map((id) => {
    const result = GENRES.find((genre) => genre.id === id);
    genres_array.push(result.name);
    return genres_array;
  });

  console.log(genres_array.toString());
  return genres_array.toString();
};

const AddMovieButton = (props) => {
  const { db } = useMongoDB();

  const onAddMovie = async ({
    id,
    title,
    genre_ids,
    poster_path,
    overview,
    vote_average,
  }) => {
    console.log(
      `adding `,
      id,
      title,
      genre_ids,
      poster_path,
      overview,
      vote_average
    );

    const genres = getGenres(genre_ids);
    await db
      .insertOne({
        id: id,
        title: title,
        genres: genres,
        posterpath: poster_path,
        overview: overview,
        voteaverage: Number(vote_average),
        watched: false,
      })
      .then((result) =>
        console.log(`Successfully inserted item with _id: ${result.insertedId}`)
      )
      .catch((err) => console.error(`Failed to insert item: ${err}`));
  };

  return (
    <div
      key={props.movie.id}
      className={classes.moviebutton}
      onClick={onAddMovie.bind(null, props.movie)}
    >
      <div className={classes.title}>{props.movie.title}</div>
      <div className={classes.vote}>{props.movie.vote_average}</div>
    </div>
  );
};

export default AddMovieButton;
