import * as Realm from "realm-web";

// Declare Schema
const MovieSchema  = {
    name: 'Movies',
    properties: {
        id:'int',
        title: 'string',
        genres:'string',
        overview:'string',
        posterpath:'string',
        voteaverage:  'int',
        watched: 'boolean'
    }
};

// Create realm
let realm = new Realm({schema: [MovieSchema], schemaVersion: 1});



let addMovies = () => {
    for(let i = 0; i < 3; i++){
      realm.write(() => {
        const movie = realm.create('Movie', {
          title: 'Harry Putter ' + i,
          overview:  "bla bla",
        });
      });
    }
  }

// Export the realm
export {addMovies};
