import React, { useState } from 'react';
import AsyncSelect from "react-select";

import makeAnimated from "react-select/animated";

const Lycos = () => {
    const [query, setQuery] = useState("");

      //get animated components wrapper
  const animatedComponents = makeAnimated();

     // fetch filtered search results for dropdown
  const loadOptions = () => {
    return fetch(`https://api.themoviedb.org/3/search/movie?api_key=5dcf7f28a88be0edc01bbbde06f024ab&language=en-US&query=${query}&page=1&include_adult=false`)
    .then((res) => res.json());
  };

    return (    <>
        <AsyncSelect
          cacheOptions
          components={animatedComponents}
          getOptionLabel={(e) => e.title}
          getOptionValue={(e) => e.id}
          loadOptions={loadOptions}
          onInputChange={(value) => setQuery(value)}
        //   onChange={(value) => setCollabs(value)}
        />
      </>)
}

export default Lycos;
