import React, { useState } from "react";
import Dropdown from "./Dropdown";
import classes from "./SearchMovie.module.css";

import { Pane, SearchInput } from "evergreen-ui";

export default function SearchMovie(props) {
  const [query, setQuery] = useState("");
  const [newMovie, setNewMovie] = useState({});


  const onSubmitHandler = () => {
    console.log("adding Movie ", newMovie);
    //setQuery("");
    props.onSubmitHandler(newMovie);
    // addMovie(newMovie);
    //setNewMovie();
  };

  const onChangeHandler = (event) => {
    setQuery(event.target.value);
    //setNewMovie({});
  };

  const onDropdownHandler = (movie) => {
    console.log("in SearchMovie you have selected ", movie);
    setNewMovie(movie);
    setQuery(movie.title);
  };

  return (
    <Pane className={classes.container}>
      <form onSubmit={onSubmitHandler}>
        <SearchInput
          className={classes["search-input"]}
          type="text"
          name="query"
          placeholder="i.e. Jurassic Park"
          onChange={onChangeHandler}
          value={query}
        />
        <input type="submit" value="OK" />√ç
      </form>

      {query && newMovie.length === 0 && (
        <Dropdown onDropdownHandler={onDropdownHandler} query={query} />
      )}
    </Pane>
  );
}
