import React, { useState, useEffect } from "react";
import { useMongoDB } from "./store/mongodb";
import { useRealmApp } from "./store/realm";

import LogInForm from "./components/LoginForm";
import MovieList from "./components/MovieList";
import SearchMovie from "./components/Movie/SearchMovie";

import { Pane } from "evergreen-ui";
import TestMongo from "./tests/TestMongo";

function App() {
  const { logIn, logOut, user } = useRealmApp();
  const { db } = useMongoDB();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [movies, setMovies] = useState([]);

  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    async function wrapMovieQuery() {
      if (user && db) {
        const authoredMovies = await db.collection("movies").find();
        setMovies(authoredMovies);
      }
    }
    wrapMovieQuery();
  }, [user, db, refresh]);


  async function handleLogIn() {
    await logIn(email, password);
  }

  const selectMovieHandler = () => {
      setRefresh(st => !st);
  }

  return user && db && user.state === "active" ? (
    <Pane
      width="90%"
      padding={16}
      background="purpleTint"
      borderRadius={3}
      elevation={4}
    >
      {/* <SearchMovie onClick={selectMovieHandler}/>
      <MovieList movies={movies} user={user} logOut={logOut} />
       */}
      <TestMongo />
    </Pane>
  ) : (
    <LogInForm
      email={email}
      setEmail={setEmail}
      password={password}
      setPassword={setPassword}
      handleLogIn={handleLogIn}
    />
  );
}

export default App;
