import React from "react";
import SearchMovie from "./Movie/SearchMovie";

import classes from "./Movies.module.css";
import Movie from "./Movie/Movie";

import { EasybaseProvider, useEasybase } from "easybase-react";
import { useState, useEffect } from "react";

import GENRES from "../resources/genres";

const Main = () => {
  const headers = (
    <thead>
      <tr>
        <th>watched</th>
        <th>title</th>
        <th>genre</th>
        <th>poster</th>
        <th>delete</th>
      </tr>
    </thead>
  );

  const { Frame, sync, configureFrame } = useEasybase();
  useEffect(() => {
    configureFrame({ tableName: "MOVIES", limit: 10 });
    sync();
  }, []);

  const [movies, setMovies] = useState([]);

  const movieExists = (id) => {
    return movies.some((movie) => {
      return movie.id === id;
    });
  };

  const getGenres = (genre_ids) => {
    let genres_array = [];
    genre_ids.map((id) => {
      const result = GENRES.find((genre) => genre.id === id);
      // console.log("genre",result);
      genres_array.push(result.name);
      return genres_array;
    });

    console.log(genres_array.toString());
    return genres_array.toString();
  };

  const addMovie = (movie) => {
    if (movieExists(movie.id)) {
      console.log(`duplicate!`, movie);
      return;
    }

    let genres = getGenres(movie.genre_ids);

    let newMovie = {
      ...movie,
      genres: genres,
      watched: false,
    };

    setMovies([...movies, newMovie]);

    Frame().push({
        id:newMovie.id,
        title: newMovie.title,
        genres: newMovie.genres,
        'poster-path': newMovie['poster-path'],
        'vote-average': newMovie['vote-average'],
        overview: newMovie.overview,
        watched: false
      })

      sync();
  };

  const delMovie = (id) => {
    setMovies(movies.filter((movie) => movie.id !== id));
  };

  const markWatched = (id) => {
    setMovies(
      movies.map((movie) =>
        movie.id === id ? { ...movie, watched: !movie.watched } : movie
      )
    );


  };

  return (
    <div className={classes.container}>
      <SearchMovie onSelect={addMovie} />
      {/* <Movies movies={Frame()} delMovie={delMovie} markWatched={markWatched} /> */}

      <table className={classes.movies}>
        {headers}
        <tbody>
          {Frame().map((movie) => {
            return (
              <Movie
                key={movie.id}
                movie={movie}
                delMovie={delMovie}
                markWatched={markWatched}
              />
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default Main;
