import { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import "./App.css";
import Movies from "./components/Movies";
import MovieForm from "./components/Movie/MovieForm";
import SearchMovie from "./components/Movie/SearchMovie";
import DUMMY_MOVIES from "./resources/dummy_movies";

const App = () => {
  const [movies, setMovies] = useState(DUMMY_MOVIES);
  const addMovie = (movie) => {
    let newMovie = {
      id: movie.id,
      // uuidv4(),
      title:movie.title,
      genre:movie.genre,
      watched:false
    };

    // [...] = spread operator (copy items)
    // Used because we can't (and shouldn't) change state values directly
    setMovies([...movies, newMovie]);
  };

  const delMovie = (id) => {
    setMovies(movies.filter((movie) => movie.id !== id));
  };

  const markWatched = (id) => {
    setMovies(
      movies.map((movie) =>
      movie.id === id ? { ...movie, watched: !movie.watched } : movie
      )
    );
  };

  return (
    <div>
      <SearchMovie onSelect={addMovie}/>
      {/* <MovieForm addMovie={addMovie} /> */}
      <Movies movies={movies} delMovie={delMovie} markWatched={markWatched} />
    </div>
  );
};

export default App;
